import jsQR from "jsqr";
export default {
  name: "vue-qr-reader",
  props: {
    useBackCamera: {
      type: Boolean,
      default: true
    },
    stopOnScanned: {
      type: Boolean,
      default: true
    },
    drawOnFound: {
      type: Boolean,
      default: true
    },
    lineColor: {
      type: String,
      default: "#FF3B58"
    },
    lineWidth: {
      type: Number,
      default: 2
    },
    videoWidth: {
      type: Number,
      default: 120
    },
    videoHeight: {
      type: Number,
      default: 120
    },
    responsive: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      showPlay: false,
      containerWidth: null,
      active: false
    };
  },
  computed: {
    videoWH: function videoWH() {
      if (this.containerWidth) {
        var width = this.containerWidth;
        var height = width * 0.75;
        return {
          width: width,
          height: height
        };
      }

      return {
        width: 100,
        height: 100
      }; //return { width: this.videoWidth, height: this.videoHeight };
    }
  },
  methods: {
    drawLine: function drawLine(begin, end) {
      this.canvas.beginPath();
      this.canvas.moveTo(begin.x, begin.y);
      this.canvas.lineTo(end.x, end.y);
      this.canvas.lineWidth = this.lineWidth;
      this.canvas.strokeStyle = this.lineColor;
      this.canvas.stroke();
    },
    drawBox: function drawBox(l) {
      if (this.drawOnFound) {
        this.drawLine(l.topLeftCorner, l.topRightCorner);
        this.drawLine(l.topRightCorner, l.bottomRightCorner);
        this.drawLine(l.bottomRightCorner, l.bottomLeftCorner);
        this.drawLine(l.bottomLeftCorner, l.topLeftCorner);
      }
    },
    tick: function tick() {
      if (this.$refs.video && this.$refs.video.readyState === this.$refs.video.HAVE_ENOUGH_DATA) {
        this.$refs.canvas.height = this.videoWH.height;
        this.$refs.canvas.width = this.videoWH.width;
        this.canvas.drawImage(this.$refs.video, 0, 0, this.$refs.canvas.width, this.$refs.canvas.height);
        var imageData = this.canvas.getImageData(0, 0, this.$refs.canvas.width, this.$refs.canvas.height);
        var code = false;

        try {
          code = jsQR(imageData.data, imageData.width, imageData.height);
        } catch (e) {
          // sometimes JSQR may fail, but we can carry on.
          console.error(e);
        }

        if (code) {
          this.drawBox(code.location);
          this.found(code.data);
        }
      }

      this.run();
    },
    setup: function setup() {
      var _this = this;

      if (this.responsive) {
        this.$nextTick(function () {
          _this.containerWidth = _this.$refs.container.clientWidth;
        });
      }

      if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
        this.previousCode = null;
        this.parity = 0;
        this.active = true;
        this.canvas = this.$refs.canvas.getContext("2d");
        var facingMode = this.useBackCamera ? {
          exact: "environment"
        } : "user";

        var handleSuccess = function handleSuccess(stream) {
          _this.$refs.video.srcObject = stream;

          var playPromise = _this.$refs.video.play();

          playPromise.catch(function () {
            return _this.showPlay = true;
          });
          playPromise.then(_this.run);
        };

        navigator.mediaDevices.getUserMedia({
          video: {
            facingMode: facingMode
          }
        }).then(handleSuccess).catch(function () {
          navigator.mediaDevices.getUserMedia({
            video: true
          }).then(handleSuccess);
        });
      }
    },
    run: function run() {
      var _this2 = this;

      if (this.active) {
        var playPromise = this.$refs.video.play();
        playPromise.catch(function () {
          return _this2.showPlay = true;
        });
        window.requestAnimationFrame(this.tick); //this.$refs.video.play();
        //this.showPlay = true;
      }
    },
    found: function found(code) {
      if (this.previousCode !== code) {
        this.previousCode = code;
      } else if (this.previousCode === code) {
        this.parity += 1;
      }

      if (this.parity > 2) {
        this.active = this.stopOnScanned ? false : true;
        this.parity = 0;
        this.$emit("code-scanned", code);
        this.$emit('imgOpen'); // this.$refs.video.stop();
        // this.fullStop();
        // this.showPlay = false;
        //this.$refs.canvas.style.display = 'none';

        this.$refs.video.style.display = 'none';
      }
    },
    fullStop: function fullStop() {
      if (this.$refs.video && this.$refs.video.srcObject) {
        this.$refs.video.srcObject.getTracks().forEach(function (t) {
          return t.stop();
        });
      }
    }
  },
  mounted: function mounted() {
    this.setup(); // const $asdru = this.run();
  },
  beforeDestroy: function beforeDestroy() {
    this.fullStop();
  },
  watch: {
    active: {
      immediate: true,
      handler: function handler(active) {
        if (!active) {
          this.fullStop();
        }
      }
    }
  }
};
